Layer.Datasource:
&datasource-settings;
(SELECT ST_LineMerge(ST_Collect(way)) AS way, highway, waterway, name, min(short_name) AS short_name, min(shortest_name) AS shortest_name FROM planet_osm_line WHERE name IS NOT NULL AND (highway IS NOT NULL OR waterway IS NOT NULL) AND way && ST_Expand(!bbox!, 500) GROUP BY name, highway, waterway ORDER BY highway NOT IN ('trunk', 'motorway', 'primary'), highway <> 'secondary', highway <> 'tertiary' ) AS "nazwy_drog"
way

&datasource-settings;
(SELECT way FROM planet_osm_line WHERE oneway IN ('1', 'yes', 'true') AND highway IS NOT NULL) AS "oneway_drog"
way

&datasource-settings;
(SELECT way, name FROM places_point WHERE place = 'country' AND way && !bbox! ORDER BY population DESC NULLS LAST) AS "miejsca"
way

&datasource-settings;
(SELECT way, name, short_name, shortest_name, population, place, capital FROM places_point WHERE way && !bbox! AND (place = 'city' OR place = 'town') ORDER BY place ASC, population DESC NULLS LAST) AS "miejsca"
way

&datasource-settings;
(SELECT way, name, short_name, shortest_name, population, place, capital FROM places_point WHERE way && !bbox! AND (place = 'city' OR place = 'town') ORDER BY place ASC, population DESC NULLS LAST) AS "miejsca"
way

&datasource-settings;
(SELECT way, name, short_name, shortest_name, population, place, capital FROM planet_osm_point WHERE way && !bbox! AND place IS NOT NULL ORDER BY place <> 'city' ASC, place <> 'town' ASC, place <> 'suburb' ASC, place <> 'village' ASC, population DESC NULLS LAST) as "miejsca"
way
